from sentence_transformers import SentenceTransformer
import logging
import umap
import pickle
import os

UMAP3_PATH = "umap3_model.pkl"
UMAP5_PATH = "umap5_model.pkl"


def fit_umap(embeddings, UMAP_PATH, n_components=3):
  print("attempting fit defintion")
  reducer = umap.UMAP(n_components=n_components, random_state=42)
  new_embedding = reducer.fit_transform(embeddings)
  with open(UMAP_PATH, "wb") as f:
    pickle.dump(reducer, f)
  return new_embedding

def load_umap_model(UMAP_PATH):
  print("attempting to load defintion")
  with open(UMAP_PATH, "rb") as f:
    reducer = pickle.load(f)
  return reducer

if (not(os.path.exists(UMAP3_PATH | UMAP5_PATH))):
  print("attempting to get umap")
  model = SentenceTransformer("all-MiniLM-L6-v2")

  batch_size = 64
  all_embeddings = []


  print("running embed on range")
  for i in range(0, len(messages), batch_size):
      batch = messages[i:i+batch_size]
      batch_embeddings = model.encode(batch)
      all_embeddings.extend(batch_embeddings)
      logging.info("success " + str(i))

  print("embed complete")


  fit_umap(all_embeddings, UMAP3_PATH)
  fit_umap(all_embeddings, UMAP5_PATH)
  print("maps complete")




